frappe.ui.form.on('Customer', {
	refresh: function(frm) {
		// your code here
		if (frm.selected_doc.country_code == 'KR' ||  frm.selected_doc.country_code == 'kr'){
		frm.add_custom_button(__('Home Tax Korea'),function(){

		    //common_doc.common_doc.doctype.currency_exchange_rate.api.get_calendar 

            let country_code = frm.selected_doc.country_code;
            let tax_id = frm.selected_doc.tax_id;

            frappe.call({
				method: "common_doc.common_doc.doctype.currency_exchange_rate.api.get_tax_info", 
				args: {
					'country_code':country_code,
					'bizno' : tax_id
				},
				callback: function(r) {
					console.log(r)
					//cur_frm.exchange_rate = r.message.exchange_rate;
					if(r.message) {
						cur_frm.set_value('home_tax_msg',r.message.home_tax_msg);
						cur_frm.set_value('tax_id',r.message.tax_id);
						cur_frm.set_value('taxation_type',r.message.taxation_type);
						cur_frm.set_value('home_tax_date',r.message.home_tax_date);
						cur_frm.set_value('home_tax_yn',r.message.home_tax_yn);

						return;

						}
				}
			})

		}, __("Get Info from API")    );
		}
	}
})

